import mongoose from "mongoose";

const userSchema = new mongoose.Schema({
    username: String,
    name: String,
    passwordHash: String,
    location: { type: String, default: 'Unknown' },
    token: String,
    role: {
        type: String,
        // enum:[type],
        default: "User"
    }
})


export default mongoose.model('User', userSchema, 'users');